namespace TypeOfTheBrowser{using System;using System.Web.UI;public partial class BrowserInfo:Page{protected void Page_Load(object sender,EventArgs e){this.output.Text="Browser Type: "+Request.Browser.Type+"<br/>";string ip=this.Request.UserHostAddress;/* ::1 means  localhost:port/ */if(ip=="::1"){ip=this.Request.Headers["host"];}this.output.Text+=string.Format("Your ip is: {0} <br/>", ip);}}}   // Have fun! :D